#include<iostream>
#include<stdio.h>
#include<graphics.h>
#include<math.h>
#include<vector>
#define PI 3.

using namespace std;



void draw(void);
void setup(void);

float angle = 0;


vector<float> wave;


int main(){
	setup();
//	int i = 0;
	while(true){
		cleardevice();
		draw();
//		i++;
	}
	getch();
}


void setup(){
	initwindow(1080,720);
	printf("%d %d",getmaxx(),getmaxy());
}

void draw(){
		float x = 120,y=getmaxy()/2;
		for(int i=0;i<3;i++){
			float prevX = x;
			float prevY = y;
			int n = i*2+1;
			float radius = 50 * (4 / (n * PI));;
			x+= radius*cos(n*angle);
			y+= radius*sin(n*angle);
			setcolor(8);
			circle(prevX,prevY,radius);
			setcolor(15);
			line(prevX,prevY,x,y);
		}	
	
		wave.insert(wave.begin(),y);
		line(x,y,250,wave[0]);
		
		for(int i=0;i<wave.size();i++){
			putpixel(i+250,wave[i],15);
		}
		
		if(wave.size()>500){
			wave.pop_back();
		}
		
		delay(1);
		angle+=0.03;
}
